<?php if(!defined('ABSPATH')) die(); ?><?php foreach( $wbrptGeneralSettings[ 'designList' ][ $value[ 'design' ] ][ 'colorList' ] as $colorName => $colorValue ) { ?><?php $colorList = '#' . implode( ', #', $colorValue[ 'icon' ] ); ?><label title="<?php foreach( $colorValue[ 'vars' ] as $variableName => $variableValue ) { echo ( isset( $wbrptGeneralSettings[ 'colorVariableList' ][ $variableName ] ) ? $wbrptGeneralSettings[ 'colorVariableList' ][ $variableName ] : ucfirst( str_replace( array( '$', '-' ), array( '', ' ' ), $variableName ) ) ) . ': #' . strtoupper( $variableValue ) . '&#013;'; } ?>"><input type="radio" name="wbrptColor" value="<?php echo $colorName; ?>"<?php if( $value[ 'color' ] == $colorName ) echo ' checked'; ?>><span style="background: #<?php echo $colorValue[ 'icon' ][ 0 ]; ?>;<?php if( count( $colorValue[ 'icon' ] ) > 1 ) { ?>background: -webkit-linear-gradient(left,<?php echo $colorList; ?>);background: -o-linear-gradient(left,<?php echo $colorList; ?>);background: -moz-linear-gradient(left,<?php echo $colorList; ?>);background: linear-gradient(to right,<?php echo $colorList; ?>);<?php } ?>"></span></label><?php } ?><label id="wbrpt-new-color-button" title="<?php echo __('Add a new color'); ?>"><span><i class="fa fa-eyedropper"></i></span></label>